interface BulkOperationResult {
  operation_id: string;
  type: 'user_import' | 'user_update' | 'user_delete';
  status: 'pending' | 'running' | 'completed' | 'failed' | 'paused';
  total_records: number;
  processed_records: number;
  successful_records: number;
  failed_records: number;
  created_at: string;
  started_at?: string;
  completed_at?: string;
  errors: Array<{
    row: number;
    field?: string;
    message: string;
    data?: Record<string, unknown>;
  }>;
  warnings: Array<{
    row: number;
    field?: string;
    message: string;
    data?: Record<string, unknown>;
  }>;
}

interface ValidationResult {
  is_valid: boolean;
  valid_count: number;
  invalid_count: number;
  total_count: number;
  errors: Array<{
    row: number;
    field?: string;
    message: string;
    data?: Record<string, unknown>;
  }>;
  warnings: Array<{
    row: number;
    field?: string;
    message: string;
    data?: Record<string, unknown>;
  }>;
  preview: Array<{
    row: number;
    email: string;
    first_name: string;
    last_name: string;
    roles?: string[];
  }>;
}

interface UploadProgress {
  loaded: number;
  total: number;
  percentage: number;
}

