const StatusBadge: FC<{ status: string }> = ({ status }) => {
  const getStatusConfig = (status: string) => {
    switch (status) {
      case 'pending':
        return {
          color: 'bg-gray-100 text-gray-800 border-gray-200',
          icon: AlertCircle,
        };
      case 'running':
        return {
          color: 'bg-blue-100 text-blue-800 border-blue-200',
          icon: Play,
        };
      case 'completed':
        return {
          color: 'bg-green-100 text-green-800 border-green-200',
          icon: CheckCircle,
        };
      case 'failed':
        return {
          color: 'bg-red-100 text-red-800 border-red-200',
          icon: XCircle,
        };
      case 'paused':
        return {
          color: 'bg-yellow-100 text-yellow-800 border-yellow-200',
          icon: Pause,
        };
      default:
        return {
          color: 'bg-gray-100 text-gray-800 border-gray-200',
          icon: AlertCircle,
        };
    }
  };

  const { color, icon: Icon } = getStatusConfig(status);

  return (
    <span
      className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium border ${color}`}
    >
      <Icon className="w-3 h-3 mr-1" />
      {status}
    </span>
  );
};

const ProgressBar: FC<{
  current: number;
  total: number;
  status: string;
  showPercentage?: boolean;
}> = ({ current, total, status, showPercentage = true }) => {
  const percentage = total > 0 ? Math.round((current / total) * 100) : 0;

  const getBarColor = () => {
    switch (status) {
      case 'completed':
        return 'bg-green-500';
      case 'failed':
        return 'bg-red-500';
      case 'running':
        return 'bg-blue-500';
      case 'paused':
        return 'bg-yellow-500';
      default:
        return 'bg-gray-500';
    }
  };

  return (
    <div className="w-full">
      <div className="flex justify-between text-sm text-gray-600 mb-1">
        <span>
          {current} / {total}
        </span>
        {showPercentage && <span>{percentage}%</span>}
      </div>
      <div className="w-full bg-gray-200 rounded-full h-2">
        <div
          className={`h-2 rounded-full transition-all duration-300 ${getBarColor()}`}
          style={{ width: `${percentage}%` }}
        />
      </div>
    </div>
  );
};

const FileUploadArea: FC<{
  onFileSelect: (file: File) => void;
  isUploading: boolean;
  progress?: UploadProgress;
}> = ({ onFileSelect, isUploading, progress }) => {
  const fileInputRef = useRef<HTMLInputElement>(null);
  const [isDragOver, setIsDragOver] = useState(false);

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(true);
  };

  const handleDragLeave = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(false);
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(false);

    const files = e.dataTransfer.files;
    if (files.length > 0) {
      const file = files[0];
      if (file.type === 'text/csv' || file.name.endsWith('.csv')) {
        onFileSelect(file);
      }
    }
  };

  const handleFileInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const files = e.target.files;
    if (files && files.length > 0) {
      onFileSelect(files[0]);
    }
  };

  if (isUploading && progress) {
    return (
      <div className="border-2 border-blue-300 border-dashed rounded-lg p-8 text-center bg-blue-50">
        <FileUp className="w-12 h-12 text-blue-400 mx-auto mb-4" />
        <p className="text-lg font-medium text-gray-900 mb-2">Uploading file...</p>
        <div className="max-w-xs mx-auto">
          <ProgressBar
            current={progress.loaded}
            total={progress.total}
            status="running"
            showPercentage={true}
          />
        </div>
      </div>
    );
  }

  return (
    <button
      type="button"
      className={`w-full border-2 border-dashed rounded-lg p-8 text-center transition-colors cursor-pointer ${
        isDragOver ? 'border-blue-400 bg-blue-50' : 'border-gray-300 bg-white hover:border-gray-400'
      }`}
      onDragOver={handleDragOver}
      onDragLeave={handleDragLeave}
      onDrop={handleDrop}
      onClick={() => fileInputRef.current?.click()}
    >
      <Upload className="w-12 h-12 text-gray-400 mx-auto mb-4" />
      <h3 className="text-lg font-medium text-gray-900 mb-2">Upload CSV File</h3>
      <p className="text-gray-600 mb-4">Drag and drop your CSV file here, or click to browse</p>
      <p className="text-sm text-gray-500">
        Supports CSV files with columns: email, first_name, last_name, roles (optional)
      </p>
      <input
        ref={fileInputRef}
        type="file"
        accept=".csv"
        onChange={handleFileInputChange}
        className="hidden"
      />
    </button>
  );
};

