interface DataExportRequest {
  request_id: string;
  user_id: string;
  email: string;
  request_type: 'personal_data' | 'activity_log' | 'full_profile';
  status: 'pending' | 'processing' | 'ready' | 'downloaded' | 'expired' | 'failed';
  created_at: string;
  processed_at?: string;
  expires_at?: string;
  download_url?: string;
  file_size?: number;
}

interface DeletionRequest {
  request_id: string;
  user_id: string;
  email: string;
  reason: string;
  status: 'pending' | 'approved' | 'processing' | 'completed' | 'rejected' | 'cancelled';
  created_at: string;
  scheduled_date?: string;
  completed_at?: string;
  approved_by?: string;
  rejection_reason?: string;
  consent_confirmed: boolean;
  data_retention_period: number;
}

interface ConsentRecord {
  consent_id: string;
  user_id: string;
  email: string;
  consent_type: 'data_processing' | 'marketing' | 'analytics' | 'cookies';
  granted: boolean;
  granted_at?: string;
  withdrawn_at?: string;
  ip_address: string;
  user_agent: string;
  legal_basis: string;
}

interface GDPRStats {
  total_export_requests: number;
  pending_exports: number;
  total_deletion_requests: number;
  pending_deletions: number;
  active_consents: number;
  withdrawn_consents: number;
}

