const StatusBadge: FC<{ status: string; type: 'export' | 'deletion' }> = ({ status, type }) => {
  const getStatusConfig = (status: string, type: string) => {
    if (type === 'export') {
      switch (status) {
        case 'pending':
          return {
            color:
              'bg-[var(--color-warning-light)] text-[var(--color-warning)] border-[var(--color-warning)]',
            icon: Clock,
            label: 'Pending',
          };
        case 'processing':
          return {
            color:
              'bg-[var(--color-primary-light)] text-[var(--color-primary)] border-[var(--color-primary)]',
            icon: RefreshCw,
            label: 'Processing',
          };
        case 'ready':
          return {
            color:
              'bg-[var(--color-success-light)] text-[var(--color-success)] border-[var(--color-success)]',
            icon: CheckCircle,
            label: 'Ready',
          };
        case 'downloaded':
          return {
            color:
              'bg-[var(--color-surface-secondary)] text-[var(--color-text-primary)] border-[var(--color-border)]',
            icon: Download,
            label: 'Downloaded',
          };
        case 'expired':
          return {
            color:
              'bg-[var(--color-error-light)] text-[var(--color-error)] border-[var(--color-error)]',
            icon: XCircle,
            label: 'Expired',
          };
        case 'failed':
          return {
            color:
              'bg-[var(--color-error-light)] text-[var(--color-error)] border-[var(--color-error)]',
            icon: XCircle,
            label: 'Failed',
          };
        default:
          return {
            color:
              'bg-[var(--color-surface-secondary)] text-[var(--color-text-primary)] border-[var(--color-border)]',
            icon: Clock,
            label: 'Unknown',
          };
      }
    } else {
      switch (status) {
        case 'pending':
          return {
            color:
              'bg-[var(--color-warning-light)] text-[var(--color-warning)] border-[var(--color-warning)]',
            icon: Clock,
            label: 'Pending',
          };
        case 'approved':
          return {
            color:
              'bg-[var(--color-primary-light)] text-[var(--color-primary)] border-[var(--color-primary)]',
            icon: CheckCircle,
            label: 'Approved',
          };
        case 'processing':
          return {
            color:
              'bg-[var(--color-primary-light)] text-[var(--color-primary)] border-[var(--color-primary)]',
            icon: RefreshCw,
            label: 'Processing',
          };
        case 'completed':
          return {
            color:
              'bg-[var(--color-success-light)] text-[var(--color-success)] border-[var(--color-success)]',
            icon: CheckCircle,
            label: 'Completed',
          };
        case 'rejected':
          return {
            color:
              'bg-[var(--color-error-light)] text-[var(--color-error)] border-[var(--color-error)]',
            icon: XCircle,
            label: 'Rejected',
          };
        case 'cancelled':
          return {
            color:
              'bg-[var(--color-surface-secondary)] text-[var(--color-text-primary)] border-[var(--color-border)]',
            icon: XCircle,
            label: 'Cancelled',
          };
        default:
          return {
            color:
              'bg-[var(--color-surface-secondary)] text-[var(--color-text-primary)] border-[var(--color-border)]',
            icon: Clock,
            label: 'Unknown',
          };
      }
    }
  };

  const { color, icon: Icon, label } = getStatusConfig(status, type);

  return (
    <span
      className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium border ${color}`}
      role="status"
      aria-label={`${type === 'export' ? 'Export' : 'Deletion'} request status: ${label}`}
    >
      <Icon className="icon-xs mr-1" aria-hidden="true" />
      {status}
    </span>
  );
};

