const GDPRCompliancePage: FC = () => {
  const { hasPermission } = useAuth();
  const { error, handleError, clearError } = useErrorHandler();
  const { toast } = useToast();

  // State
  const [exportRequests, setExportRequests] = useState<DataExportRequest[]>([]);
  const [deletionRequests, setDeletionRequests] = useState<DeletionRequest[]>([]);
  const [consentRecords, setConsentRecords] = useState<ConsentRecord[]>([]);
  const [stats, setStats] = useState<GDPRStats | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [activeTab, setActiveTab] = useState<'exports' | 'deletions' | 'consents'>('exports');
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');

  // Check permissions
  const canManageGDPR = hasPermission('admin') || hasPermission('gdpr:manage');

  // ============================================================================
  // Data Loading Functions
  // ============================================================================
  // React 19 Compiler handles memoization

  const loadGDPRData = async () => {
    if (!canManageGDPR) return;

    setIsLoading(true);
    try {
      const [exports, deletions, consents, gdprStats] = await Promise.all([
        adminService.getDataExportRequests(),
        adminService.getDeletionRequests(),
        adminService.getConsentRecords(),
        adminService.getGDPRStats(),
      ]);

      setExportRequests(exports);
      setDeletionRequests(deletions);
      setConsentRecords(consents);
      setStats(gdprStats);
    } catch (error) {
      handleError(error, 'Failed to load GDPR data');
      toast.error('Failed to load GDPR data');
    } finally {
      setIsLoading(false);
    }
  };

  // ============================================================================
  // Action Functions
  // ============================================================================

  const handleDownloadExport = async (requestId: string) => {
    try {
      await adminService.downloadDataExport(requestId);
      toast.success('Data export downloaded successfully');
    } catch (error) {
      handleError(error, 'Failed to download export');
      toast.error('Failed to download export');
    }
  };

  const handleApproveDeletion = async (requestId: string) => {
    try {
      await adminService.approveDeletionRequest(requestId);
      await loadGDPRData();
      toast.success('Deletion request approved');
    } catch (error) {
      handleError(error, 'Failed to approve deletion request');
      toast.error('Failed to approve deletion request');
    }
  };

  const handleRejectDeletion = async (requestId: string, reason: string) => {
    try {
      await adminService.rejectDeletionRequest(requestId, reason);
      await loadGDPRData();
      toast.success('Deletion request rejected');
    } catch (error) {
      handleError(error, 'Failed to reject deletion request');
      toast.error('Failed to reject deletion request');
    }
  };

  // ============================================================================
  // Filter Functions
  // ============================================================================

  const filteredExportRequests = exportRequests.filter((request) => {
    const matchesSearch =
      request.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
      request.user_id.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesStatus = statusFilter === 'all' || request.status === statusFilter;
    return matchesSearch && matchesStatus;
  });

  const filteredDeletionRequests = deletionRequests.filter((request) => {
    const matchesSearch =
      request.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
      request.user_id.toLowerCase().includes(searchTerm.toLowerCase()) ||
      request.reason.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesStatus = statusFilter === 'all' || request.status === statusFilter;
    return matchesSearch && matchesStatus;
  });

  const filteredConsentRecords = consentRecords.filter((record) => {
    const matchesSearch =
      record.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
      record.user_id.toLowerCase().includes(searchTerm.toLowerCase());
    return matchesSearch;
  });

  // ============================================================================
  // Effects
  // ============================================================================

  useEffect(() => {
    loadGDPRData();
  }, [loadGDPRData]);

  // ============================================================================
  // Render
  // ============================================================================

  if (!canManageGDPR) {
    return (
      <div className="page-wrapper">
        <div className="container-narrow">
          <div className="text-center py-12">
            <Shield className="w-12 h-12 text-[color:var(--color-text-tertiary)] mx-auto mb-4" />
            <h3 className="text-lg font-medium text-[color:var(--color-text-primary)] mb-2">
              Access Restricted
            </h3>
            <p className="text-[var(--color-text-secondary)]">
              You don't have permission to manage GDPR compliance.
            </p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <>
      <PageMetadata
        title="GDPR Compliance Center"
        description="Manage data export, deletion, and consent requests to keep your organization compliant with GDPR mandates."
        keywords="gdpr compliance, data export requests, account deletion, consent management"
      />
      <div className="page-wrapper">
        <div className="container-full">
          {/* Header */}
          <div className="mb-8">
            <Breadcrumb />
            <div className="mt-4 flex items-center justify-between">
              <div>
                <h1 className="text-2xl font-bold text-[var(--color-text-primary)]">
                  GDPR Compliance
                </h1>
                <p className="text-[color:var(--color-text-secondary)] mt-1">
                  Data protection and privacy compliance management
                </p>
              </div>
              <div className="flex space-x-3">
                <button
                  onClick={loadGDPRData}
                  disabled={isLoading}
                  className="inline-flex items-center px-4 py-2 border border-[color:var(--color-border-primary)] rounded-md shadow-sm text-sm font-medium text-[color:var(--color-text-primary)] bg-[var(--color-surface-primary)] hover:bg-[color:var(--color-background-secondary)] disabled:opacity-50"
                >
                  <RefreshCw className={`icon-sm mr-2 ${isLoading ? 'animate-spin' : ''}`} />
                  Refresh
                </button>
              </div>
            </div>
          </div>

          {/* Error Alert */}
          {error && (
            <div className="mb-6">
              <ErrorAlert error={error} onDismiss={clearError} />
            </div>
          )}

          {/* Stats Cards */}
          {stats && (
            <div
              className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-6 gap-4 mb-8"
              role="region"
              aria-label="GDPR statistics summary"
            >
              <div
                className="bg-[var(--color-surface-primary)] rounded-lg shadow-sm border p-4"
                role="status"
                aria-label={`${stats.total_data_breaches} data breaches`}
              >
                <div className="flex items-center">
                  <ShieldAlert
                    className="icon-lg text-[color:var(--color-text-secondary)] mr-3"
                    aria-hidden="true"
                  />
                  <div>
                    <p className="text-xs font-medium text-[var(--color-text-secondary)]">
                      Data Breaches
                    </p>
                    <p className="text-lg font-semibold text-[var(--color-text-primary)]">
                      {stats.total_data_breaches}
                    </p>
                  </div>
                </div>
              </div>
              <div
                className="bg-[var(--color-surface-primary)] rounded-lg shadow-sm border p-4"
                role="status"
                aria-label={`${stats.total_export_requests} export requests`}
              >
                <div className="flex items-center">
                  <Download
                    className="icon-lg text-[color:var(--color-primary)] mr-3"
                    aria-hidden="true"
                  />
                  <div>
                    <p className="text-xs font-medium text-[var(--color-text-secondary)]">
                      Export Requests
                    </p>
                    <p className="text-lg font-semibold text-[var(--color-text-primary)]">
                      {stats.total_export_requests}
                    </p>
                  </div>
                </div>
              </div>
              <div
                className="bg-[var(--color-surface-primary)] rounded-lg shadow-sm border p-4"
                role="status"
                aria-label={`${stats.pending_export} pending export requests`}
              >
                <div className="flex items-center">
                  <Clock
                    className="icon-lg text-[color:var(--color-warning)] mr-3"
                    aria-hidden="true"
                  />
                  <div>
                    <p className="text-xs font-medium text-[var(--color-text-secondary)]">
                      Pending Exports
                    </p>
                    <p className="text-lg font-semibold text-[var(--color-text-primary)]">
                      {stats.pending_exports}
                    </p>
                  </div>
                </div>
              </div>
              <div
                className="bg-[var(--color-surface-primary)] rounded-lg shadow-sm border p-4"
                role="status"
                aria-label={`${stats.total_deletion_requests} deletion requests`}
              >
                <div className="flex items-center">
                  <Trash2
                    className="icon-lg text-[color:var(--color-error)] mr-3"
                    aria-hidden="true"
                  />
                  <div>
                    <p className="text-xs font-medium text-[var(--color-text-secondary)]">
                      Deletion Requests
                    </p>
                    <p className="text-lg font-semibold text-[var(--color-text-primary)]">
                      {stats.total_deletion_requests}
                    </p>
                  </div>
                </div>
              </div>
              <div
                className="bg-[var(--color-surface-primary)] rounded-lg shadow-sm border p-4"
                role="status"
                aria-label={`${stats.pending_deletion} pending deletion requests`}
              >
                <div className="flex items-center">
                  <AlertTriangle
                    className="icon-lg text-[var(--color-warning)] mr-3"
                    aria-hidden="true"
                  />
                  <div>
                    <p className="text-xs font-medium text-[var(--color-text-secondary)]">
                      Pending Deletions
                    </p>
                    <p className="text-lg font-semibold text-[var(--color-text-primary)]">
                      {stats.pending_deletions}
                    </p>
                  </div>
                </div>
              </div>
              <div
                className="bg-[var(--color-surface-primary)] rounded-lg shadow-sm border p-4"
                role="status"
                aria-label={`${stats.active_consents} active consents`}
              >
                <div className="flex items-center">
                  <CheckCircle
                    className="icon-lg text-[color:var(--color-success)] mr-3"
                    aria-hidden="true"
                  />
                  <div>
                    <p className="text-xs font-medium text-[var(--color-text-secondary)]">
                      Active Consents
                    </p>
                    <p className="text-lg font-semibold text-[var(--color-text-primary)]">
                      {stats.active_consents}
                    </p>
                  </div>
                </div>
              </div>
              <div
                className="bg-[var(--color-surface-primary)] rounded-lg shadow-sm border p-4"
                role="status"
                aria-label={`${stats.withdrawn_consents} withdrawn consents`}
              >
                <div className="flex items-center">
                  <XCircle
                    className="icon-lg text-[color:var(--color-text-secondary)] mr-3"
                    aria-hidden="true"
                  />
                  <div>
                    <p className="text-xs font-medium text-[var(--color-text-secondary)]">
                      Withdrawn
                    </p>
                    <p className="text-lg font-semibold text-[var(--color-text-primary)]">
                      {stats.withdrawn_consents}
                    </p>
                  </div>
                </div>
              </div>
            </div>
          )}

          {/* Tabs and Filters */}
          <div className="bg-[var(--color-surface-primary)] rounded-lg shadow-sm border mb-6">
            <div className="border-b border-[var(--color-border)]">
              <div className="flex items-center justify-between px-6 py-4">
                <div role="tablist" aria-label="GDPR data categories">
                  <nav className="-mb-px flex space-x-8">
                    <button
                      onClick={() => setActiveTab('exports')}
                      role="tab"
                      aria-selected={activeTab === 'exports'}
                      aria-controls="exports-panel"
                      className={`py-2 px-1 border-b-2 font-medium text-sm ${
                        activeTab === 'exports'
                          ? 'border-[var(--color-primary)] text-[var(--color-primary)]'
                          : 'border-transparent text-[var(--color-text-tertiary)] hover:text-[var(--color-text-secondary)] hover:border-[var(--color-border)]'
                      }`}
                    >
                      Data Export Requests
                    </button>
                    <button
                      onClick={() => setActiveTab('deletions')}
                      role="tab"
                      aria-selected={activeTab === 'deletions'}
                      aria-controls="deletions-panel"
                      className={`py-2 px-1 border-b-2 font-medium text-sm ${
                        activeTab === 'deletions'
                          ? 'border-[var(--color-primary)] text-[var(--color-primary)]'
                          : 'border-transparent text-[var(--color-text-tertiary)] hover:text-[var(--color-text-secondary)] hover:border-[var(--color-border)]'
                      }`}
                    >
                      Deletion Requests
                    </button>
                    <button
                      onClick={() => setActiveTab('consents')}
                      role="tab"
                      aria-selected={activeTab === 'consents'}
                      aria-controls="consents-panel"
                      className={`py-2 px-1 border-b-2 font-medium text-sm ${
                        activeTab === 'consents'
                          ? 'border-[var(--color-primary)] text-[var(--color-primary)]'
                          : 'border-transparent text-[var(--color-text-tertiary)] hover:text-[var(--color-text-secondary)] hover:border-[var(--color-border)]'
                      }`}
                    >
                      Consent Records
                    </button>
                  </nav>
                </div>

                <div className="flex space-x-4">
                  <div className="relative" role="search">
                    <Search
                      className="absolute left-3 top-1/2 transform -translate-y-1/2 text-[color:var(--color-text-tertiary)] icon-sm"
                      aria-hidden="true"
                    />
                    <input
                      type="text"
                      placeholder="Search..."
                      value={searchTerm}
                      onChange={(e) => setSearchTerm(e.target.value)}
                      aria-label="Search GDPR records"
                      className="pl-10 pr-4 py-2 border border-[color:var(--color-border-primary)] rounded-md text-sm focus:outline-none focus:ring-2 focus:ring-[color:var(--color-primary)] focus:border-[var(--color-primary)]"
                    />
                  </div>
                  {activeTab !== 'consents' && (
                    <select
                      value={statusFilter}
                      onChange={(e) => setStatusFilter(e.target.value)}
                      aria-label="Filter by status"
                      className="px-3 py-2 border border-[color:var(--color-border-primary)] rounded-md text-sm focus:outline-none focus:ring-2 focus:ring-[color:var(--color-primary)] focus:border-[var(--color-primary)]"
                    >
                      <option value="all">All Statuses</option>
                      <option value="pending">Pending</option>
                      <option value="processing">Processing</option>
                      <option value="ready">Ready</option>
                      <option value="completed">Completed</option>
                      <option value="failed">Failed</option>
                    </select>
                  )}
                </div>
              </div>
            </div>

            {/* Tab Content */}
            <div className="p-6">
              {activeTab === 'exports' && (
                <div role="tabpanel" id="exports-panel" aria-labelledby="exports-tab">
                  <ExportRequestsTable
                    requests={filteredExportRequests}
                    onDownload={handleDownloadExport}
                    onViewDetails={() => {}}
                    isLoading={isLoading}
                  />
                </div>
              )}

              {activeTab === 'deletions' && (
                <div role="tabpanel" id="deletions-panel" aria-labelledby="deletions-tab">
                  <DeletionRequestsTable
                    requests={filteredDeletionRequests}
                    onApprove={handleApproveDeletion}
                    onReject={handleRejectDeletion}
                    onViewDetails={() => {}}
                    isLoading={isLoading}
                  />
                </div>
              )}

              {activeTab === 'consents' && (
                <div role="tabpanel" id="consents-panel" aria-labelledby="consents-tab">
                  <div
                    className="overflow-x-auto"
                    role="region"
                    aria-label="Consent management records"
                  >
                    {isLoading ? (
                      <div className="space-y-4">
                        {Array.from({ length: 5 }).map((_, i) => (
                          <div
                            key={i}
                            className="flex items-center justify-between p-4 border border-[color:var(--color-border-primary)] rounded-lg"
                            role="status"
                            aria-label="Loading consent record"
                          >
                            <div className="flex items-center space-x-4">
                              <Skeleton className="h-10 w-10 rounded-full" />
                              <div>
                                <Skeleton className="h-4 w-32 mb-2" />
                                <Skeleton className="h-3 w-24" />
                              </div>
                            </div>
                            <Skeleton className="h-6 w-20" />
                          </div>
                        ))}
                      </div>
                    ) : (
                      <div className="space-y-4">
                        {filteredConsentRecords.length > 0 ? (
                          filteredConsentRecords.map((record) => (
                            <div
                              key={record.consent_id}
                              className="flex items-center justify-between p-4 border border-[color:var(--color-border-primary)] rounded-lg hover:bg-[var(--color-surface-hover)]"
                            >
                              <div className="flex items-center space-x-4">
                                <User
                                  className="w-10 h-10 text-[var(--color-text-tertiary)]"
                                  aria-hidden="true"
                                />
                                <div>
                                  <div className="font-medium text-[var(--color-text-primary)]">
                                    {record.email}
                                  </div>
                                  <div className="text-sm text-[var(--color-text-tertiary)]">
                                    {record.consent_type} {record.legal_basis}
                                  </div>
                                </div>
                              </div>
                              <div className="flex items-center space-x-3">
                                {record.granted ? (
                                  <span
                                    className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-[color:var(--color-success-100)] text-[var(--color-success)]"
                                    role="status"
                                    aria-label={`Consent granted on ${formatDate(record.granted_at!)}`}
                                  >
                                    <CheckCircle className="icon-xs mr-1" aria-hidden="true" />
                                    Granted
                                  </span>
                                ) : (
                                  <span
                                    className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-[color:var(--color-error-100)] text-[var(--color-error)]"
                                    role="status"
                                    aria-label={`Consent withdrawn on ${formatDate(record.withdrawn_at!)}`}
                                  >
                                    <XCircle className="icon-xs mr-1" aria-hidden="true" />
                                    Withdrawn
                                  </span>
                                )}
                                <div className="text-sm text-[var(--color-text-tertiary)]">
                                  {record.granted
                                    ? formatDate(record.granted_at!)
                                    : formatDate(record.withdrawn_at!)}
                                </div>
                              </div>
                            </div>
                          ))
                        ) : (
                          <div className="text-center py-12">
                            <Shield
                              className="w-12 h-12 text-[color:var(--color-text-tertiary)] mx-auto mb-4"
                              aria-hidden="true"
                            />
                            <h3 className="text-lg font-medium text-[color:var(--color-text-primary)] mb-2">
                              No consent records
                            </h3>
                            <p className="text-[var(--color-text-secondary)]">
                              No consent records match your search criteria.
                            </p>
                          </div>
                        )}
                      </div>
                    )}
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default GDPRCompliancePage;
