const StatusBadge: FC<{ status: string; type: 'reset' | 'security' | 'strength' | 'lock' }> = ({
  status,
  type,
}) => {
  const getStatusConfig = (status: string, type: string) => {
    if (type === 'reset') {
      switch (status) {
        case 'pending':
          return {
            color:
              'bg-[var(--color-warning-light)] text-[var(--color-warning)] border-[var(--color-warning)]',
            icon: Clock,
            label: 'Pending',
          };
        case 'used':
          return {
            color:
              'bg-[var(--color-success-light)] text-[var(--color-success)] border-[var(--color-success)]',
            icon: CheckCircle,
            label: 'Used',
          };
        case 'expired':
          return {
            color:
              'bg-[var(--color-error-light)] text-[var(--color-error)] border-[var(--color-error)]',
            icon: XCircle,
            label: 'Expired',
          };
        case 'cancelled':
          return {
            color:
              'bg-[var(--color-surface-secondary)] text-[var(--color-text-primary)] border-[var(--color-border)]',
            icon: XCircle,
            label: 'Cancelled',
          };
        default:
          return {
            color:
              'bg-[var(--color-surface-secondary)] text-[var(--color-text-primary)] border-[var(--color-border)]',
            icon: Clock,
            label: status,
          };
      }
    } else if (type === 'strength') {
      switch (status) {
        case 'strong':
          return {
            color:
              'bg-[var(--color-success-light)] text-[var(--color-success)] border-[var(--color-success)]',
            icon: Shield,
            label: 'Strong',
          };
        case 'good':
          return {
            color:
              'bg-[var(--color-primary-light)] text-[var(--color-primary)] border-[var(--color-primary)]',
            icon: Shield,
            label: 'Good',
          };
        case 'fair':
          return {
            color:
              'bg-[var(--color-warning-light)] text-[var(--color-warning)] border-[var(--color-warning)]',
            icon: AlertTriangle,
            label: 'Fair',
          };
        case 'weak':
          return {
            color:
              'bg-[var(--color-error-light)] text-[var(--color-error)] border-[var(--color-error)]',
            icon: AlertTriangle,
            label: 'Weak',
          };
        default:
          return {
            color:
              'bg-[var(--color-surface-secondary)] text-[var(--color-text-primary)] border-[var(--color-border)]',
            icon: Shield,
            label: status,
          };
      }
    } else if (type === 'lock') {
      return status === 'locked'
        ? {
            color:
              'bg-[var(--color-error-light)] text-[var(--color-error)] border-[var(--color-error)]',
            icon: Lock,
            label: 'Locked',
          }
        : {
            color:
              'bg-[var(--color-success-light)] text-[var(--color-success)] border-[var(--color-success)]',
            icon: Unlock,
            label: 'Unlocked',
          };
    } else {
      switch (status) {
        case 'critical':
          return {
            color:
              'bg-[var(--color-error-light)] text-[var(--color-error)] border-[var(--color-error)]',
            icon: XCircle,
            label: 'Critical',
          };
        case 'high':
          return {
            color:
              'bg-[var(--color-warning)] text-[var(--color-warning)] border-[var(--color-warning)]',
            icon: AlertTriangle,
            label: 'High',
          };
        case 'medium':
          return {
            color:
              'bg-[var(--color-warning-light)] text-[var(--color-warning)] border-[var(--color-warning)]',
            icon: AlertTriangle,
            label: 'Medium',
          };
        case 'low':
          return {
            color:
              'bg-[var(--color-success-light)] text-[var(--color-success)] border-[var(--color-success)]',
            icon: CheckCircle,
            label: 'Low',
          };
        default:
          return {
            color:
              'bg-[var(--color-surface-secondary)] text-[var(--color-text-primary)] border-[var(--color-border)]',
            icon: Clock,
            label: status,
          };
      }
    }
  };

  const { color, icon: Icon, label } = getStatusConfig(status, type);

  return (
    <span
      className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium border ${color}`}
      role="status"
      aria-label={`${type === 'reset' ? 'Reset request' : type === 'strength' ? 'Password strength' : type === 'lock' ? 'Account' : 'Security'} status: ${label}`}
    >
      <Icon className="icon-xs mr-1" aria-hidden="true" />
      {status}
    </span>
  );
};

