/**
 * Role Management Page
 *
 * Comprehensive role and permission management interface.
 * Allows CRUD operations on roles, permission assignments, and user-role relationships.
 *
 * @author Generated by GitHub Copilot
 */

import { Lock, Plus, Search, Shield, Trash2, UserPlus, X } from 'lucide-react';
import { useCallback, useEffect, useState, type FC } from 'react';

import { useAuth } from '@domains/auth/context/AuthContext';
import { useErrorHandler } from '@hooks/errors/useErrorHandler';
import { useLocalization } from '@hooks/localization/useLocalization';
import { TextInput } from '@shared/components/forms/FormComponents';
import Breadcrumb from '@shared/ui/Breadcrumb';
import ErrorAlert from '@shared/ui/ErrorAlert';
import { Skeleton } from '@shared/ui/Skeleton';
import { adminService } from '../../../services/admin-backend.service';

// ============================================================================
// Types & Interfaces
// ============================================================================

interface Role {
  role_id: string;
  role_name: string;
  description: string;
  permissions: string[];
}

interface Permission {
  permission_id: string;
  category: string;
  action: string;
  resource: string;
  description?: string;
}

interface RoleFormData {
  role_name: string;
  description: string;
  permissions: string[];
}

interface AssignRoleModalProps {
  isOpen: boolean;
  onClose: () => void;
  roles: Role[];
  onAssign: (userId: string, roleId: string, expiresAt?: string) => Promise<void>;
}

interface CreateRoleModalProps {
  isOpen: boolean;
  onClose: () => void;
  permissions: Permission[];
  onSubmit: (roleData: RoleFormData) => Promise<void>;
  isLoading: boolean;
}

// ============================================================================
// Components
// ============================================================================

const CreateRoleModal: FC<CreateRoleModalProps> = ({
  isOpen,
  onClose,
  permissions,
  onSubmit,
  isLoading,
}) => {
  const { t } = useLocalization();
  const [formData, setFormData] = useState<RoleFormData>({
    role_name: '',
    description: '',
    permissions: [],
  });
  const [selectedPermissions, setSelectedPermissions] = useState<Set<string>>(new Set());

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    await onSubmit({
      ...formData,
      permissions: Array.from(selectedPermissions),
    });
    setFormData({ role_name: '', description: '', permissions: [] });
    setSelectedPermissions(new Set());
    onClose();
  };

  const togglePermission = (permissionId: string) => {
    const newSelected = new Set(selectedPermissions);
    if (newSelected.has(permissionId)) {
      newSelected.delete(permissionId);
    } else {
      newSelected.add(permissionId);
    }
    setSelectedPermissions(newSelected);
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg shadow-xl w-full max-w-2xl max-h-[90vh] overflow-hidden">
        <div className="px-6 py-4 border-b border-gray-200">
          <div className="flex items-center justify-between">
            <h3 className="text-lg font-medium text-gray-900">{t('roles.createNewRole')}</h3>
            <button onClick={onClose} className="text-gray-400 hover:text-gray-500">
              <X className="w-6 h-6" />
            </button>
          </div>
        </div>

        <form onSubmit={handleSubmit} className="flex flex-col h-full">
          <div className="px-6 py-4 flex-1 overflow-y-auto">
            {/* Role Name */}
            <TextInput
              label={t('roles.roleName')}
              value={formData.role_name}
              onChange={(value) => setFormData({ ...formData, role_name: value })}
              placeholder={t('roles.enterRoleName')}
              required
            />

            {/* Description */}
            <div className="mb-4">
              <label htmlFor="description" className="block text-sm font-medium text-gray-700 mb-1">
                {t('roles.description')}
              </label>
              <textarea
                id="description"
                value={formData.description}
                onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                placeholder={t('roles.enterRoleDescription')}
                rows={3}
                required
              />
            </div>

            {/* Permissions */}
            <div className="mb-4">
              <label htmlFor="permissions" className="block text-sm font-medium text-gray-700 mb-2">
                {t('roles.permissions')}
              </label>
              <div className="border border-gray-300 rounded-md max-h-48 overflow-y-auto">
                {permissions.map((permission) => (
                  <div
                    key={permission.permission_id}
                    className="flex items-center p-3 border-b border-gray-100 last:border-0 hover:bg-gray-50"
                  >
                    <input
                      type="checkbox"
                      checked={selectedPermissions.has(permission.permission_id)}
                      onChange={() => togglePermission(permission.permission_id)}
                      className="mr-3 h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
                    />
                    <div className="flex-1">
                      <div className="text-sm font-medium text-gray-900">
                        {permission.action} - {permission.resource}
                      </div>
                      <div className="text-xs text-gray-500">
                        {t('roles.category')}: {permission.category}
                      </div>
                      {permission.description && (
                        <div className="text-xs text-gray-600 mt-1">{permission.description}</div>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>

          <div className="px-6 py-4 border-t border-gray-200 flex justify-end space-x-3">
            <button
              type="button"
              onClick={onClose}
              className="px-4 py-2 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-md hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
            >
              {t('roles.cancel')}
            </button>
            <button
              type="submit"
              disabled={isLoading || !formData.role_name || !formData.description}
              className="px-4 py-2 text-sm font-medium text-white bg-blue-600 border border-transparent rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 disabled:opacity-50"
            >
              {isLoading ? t('roles.creating') : t('roles.createRole')}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

const AssignRoleModal: FC<AssignRoleModalProps> = ({ isOpen, onClose, roles, onAssign }) => {
  const [userId, setUserId] = useState('');
  const [selectedRoleId, setSelectedRoleId] = useState('');
  const [expiresAt, setExpiresAt] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!userId || !selectedRoleId) return;

    setIsLoading(true);
    try {
      await onAssign(userId, selectedRoleId, expiresAt || undefined);
      setUserId('');
      setSelectedRoleId('');
      setExpiresAt('');
      onClose();
    } finally {
      setIsLoading(false);
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg shadow-xl w-full max-w-md">
        <div className="px-6 py-4 border-b border-gray-200">
          <div className="flex items-center justify-between">
            <h3 className="text-lg font-medium text-gray-900">Assign Role to User</h3>
            <button onClick={onClose} className="text-gray-400 hover:text-gray-500">
              <X className="w-6 h-6" />
            </button>
          </div>
        </div>

        <form onSubmit={handleSubmit}>
          <div className="px-6 py-4 space-y-4">
            <TextInput
              label="User ID"
              value={userId}
              onChange={setUserId}
              placeholder="Enter user ID"
              required
            />

            <div>
              <label htmlFor="roleSelect" className="block text-sm font-medium text-gray-700 mb-1">
                Role
              </label>
              <select
                id="roleSelect"
                value={selectedRoleId}
                onChange={(e) => setSelectedRoleId(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                required
              >
                <option value="">Select a role</option>
                {roles.map((role) => (
                  <option key={role.role_id} value={role.role_id}>
                    {role.role_name}
                  </option>
                ))}
              </select>
            </div>

            <div>
              <label htmlFor="expiresAt" className="block text-sm font-medium text-gray-700 mb-1">
                Expires At (Optional)
              </label>
              <input
                id="expiresAt"
                type="datetime-local"
                value={expiresAt}
                onChange={(e) => setExpiresAt(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
              />
            </div>
          </div>

          <div className="px-6 py-4 border-t border-gray-200 flex justify-end space-x-3">
            <button
              type="button"
              onClick={onClose}
              className="px-4 py-2 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-md hover:bg-gray-50"
            >
              Cancel
            </button>
            <button
              type="submit"
              disabled={isLoading || !userId || !selectedRoleId}
              className="px-4 py-2 text-sm font-medium text-white bg-blue-600 border border-transparent rounded-md hover:bg-blue-700 disabled:opacity-50"
            >
              {isLoading ? 'Assigning...' : 'Assign Role'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

// ============================================================================
// Main Component
// ============================================================================

const RoleManagementPage: FC = () => {
  const { hasPermission } = useAuth();
  const { error, handleError, clearError } = useErrorHandler();
  const { t } = useLocalization();

  // State
  const [roles, setRoles] = useState<Role[]>([]);
  const [permissions, setPermissions] = useState<Permission[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isCreateModalOpen, setIsCreateModalOpen] = useState(false);
  const [isAssignModalOpen, setIsAssignModalOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('');

  // Check permissions
  const canManageRoles = hasPermission('admin') || hasPermission('rbac:write');
  const canViewRoles = hasPermission('admin') || hasPermission('rbac:read');

  // ============================================================================
  // Data Loading Functions
  // ============================================================================

  const loadRoles = useCallback(async () => {
    if (!canViewRoles) return;

    try {
      const rolesData = await adminService.getRoles();
      setRoles(rolesData);
    } catch (error) {
      handleError(error, t('roles.failedToLoadRoles'));
    }
  }, [canViewRoles, handleError, t]);

  const loadPermissions = useCallback(async () => {
    if (!canViewRoles) return;

    try {
      const permissionsData = await adminService.getPermissions();
      setPermissions(permissionsData);
    } catch (error) {
      handleError(error, t('roles.failedToLoadPermissions'));
    }
  }, [canViewRoles, handleError, t]);

  const loadAllData = useCallback(async () => {
    setIsLoading(true);
    clearError();

    try {
      await Promise.all([loadRoles(), loadPermissions()]);
    } finally {
      setIsLoading(false);
    }
  }, [loadRoles, loadPermissions, clearError]);

  // ============================================================================
  // Action Functions
  // ============================================================================

  const handleCreateRole = useCallback(
    async (roleData: RoleFormData) => {
      try {
        await adminService.createRole(roleData);
        await loadRoles();
      } catch (error) {
        handleError(error, t('roles.failedToCreateRole'));
      }
    },
    [loadRoles, handleError, t]
  );

  const handleDeleteRole = useCallback(
    async (roleId: string) => {
      if (!confirm(t('roles.confirmDeleteRole'))) return;

      try {
        await adminService.deleteRole(roleId);
        await loadRoles();
      } catch (error) {
        handleError(error, t('roles.failedToDeleteRole'));
      }
    },
    [loadRoles, handleError, t]
  );

  const handleAssignRole = useCallback(
    async (userId: string, roleId: string, expiresAt?: string) => {
      try {
        await adminService.assignRole(userId, roleId, expiresAt);
      } catch (error) {
        handleError(error, t('roles.failedToAssignRole'));
      }
    },
    [handleError, t]
  );

  // ============================================================================
  // Filter Functions
  // ============================================================================

  const filteredRoles = roles.filter((role) => {
    const matchesSearch =
      role.role_name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      role.description.toLowerCase().includes(searchTerm.toLowerCase());
    return matchesSearch;
  });

  const categories = Array.from(new Set(permissions.map((p) => p.category)));

  const filteredPermissions = permissions.filter((permission) => {
    return !selectedCategory || permission.category === selectedCategory;
  });

  // ============================================================================
  // Effects
  // ============================================================================

  useEffect(() => {
    loadAllData();
  }, [loadAllData]);

  // ============================================================================
  // Render
  // ============================================================================

  if (!canViewRoles) {
    return (
      <div className="min-h-screen bg-gray-50 py-8">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center py-12">
            <Shield className="w-12 h-12 text-gray-400 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-gray-900 mb-2">Access Restricted</h3>
            <p className="text-gray-600">You don't have permission to view role management.</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Header */}
        <div className="mb-8">
          <Breadcrumb />
          <div className="mt-4 flex items-center justify-between">
            <div>
              <h1 className="text-2xl font-bold text-gray-900">Role Management</h1>
              <p className="text-gray-600 mt-1">Manage user roles and permissions</p>
            </div>
            {canManageRoles && (
              <div className="flex space-x-3">
                <button
                  onClick={() => setIsAssignModalOpen(true)}
                  className="inline-flex items-center px-4 py-2 border border-gray-300 rounded-md shadow-sm text-sm font-medium text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
                >
                  <UserPlus className="w-4 h-4 mr-2" />
                  {t('roles.assignRole')}
                </button>
                <button
                  onClick={() => setIsCreateModalOpen(true)}
                  className="inline-flex items-center px-4 py-2 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
                >
                  <Plus className="w-4 h-4 mr-2" />
                  {t('roles.createRole')}
                </button>
              </div>
            )}
          </div>
        </div>

        {/* Error Alert */}
        {error && (
          <div className="mb-6">
            <ErrorAlert error={error} onDismiss={clearError} />
          </div>
        )}

        {/* Search and Filters */}
        <div className="bg-white rounded-lg shadow-sm border mb-6">
          <div className="px-6 py-4">
            <div className="flex items-center space-x-4">
              <div className="flex-1">
                <div className="relative">
                  <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
                  <input
                    type="text"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    placeholder="Search roles..."
                    className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                  />
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Roles List */}
        <div className="bg-white rounded-lg shadow-sm border">
          <div className="px-6 py-4 border-b border-gray-200">
            <h3 className="text-lg font-medium text-gray-900">
              {t('roles.roles', { count: filteredRoles.length })}
            </h3>
          </div>
          <div className="p-6">
            {isLoading ? (
              <div className="space-y-4">
                {Array.from({ length: 5 }).map((_, i) => (
                  <div key={i} className="p-4 border border-gray-200 rounded-lg">
                    <Skeleton className="h-4 w-1/4 mb-2" />
                    <Skeleton className="h-3 w-3/4 mb-2" />
                    <Skeleton className="h-3 w-1/2" />
                  </div>
                ))}
              </div>
            ) : filteredRoles.length > 0 ? (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {filteredRoles.map((role) => (
                  <div
                    key={role.role_id}
                    className="p-4 border border-gray-200 rounded-lg hover:shadow-md transition-shadow"
                  >
                    <div className="flex items-start justify-between mb-3">
                      <div className="flex items-center">
                        <Shield className="w-5 h-5 text-blue-600 mr-2" />
                        <h4 className="text-lg font-medium text-gray-900">{role.role_name}</h4>
                      </div>
                      {canManageRoles && (
                        <div className="flex space-x-1">
                          <button
                            onClick={() => handleDeleteRole(role.role_id)}
                            className="text-red-600 hover:text-red-900"
                          >
                            <Trash2 className="w-4 h-4" />
                          </button>
                        </div>
                      )}
                    </div>
                    <p className="text-gray-600 text-sm mb-3">{role.description}</p>
                    <div className="flex items-center justify-between">
                      <span className="text-sm text-gray-500">
                        {t('roles.permissionsCount', { count: role.permissions.length })}
                      </span>
                      <div className="flex flex-wrap gap-1">
                        {role.permissions.slice(0, 3).map((permission) => (
                          <span
                            key={permission}
                            className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-blue-100 text-blue-800"
                          >
                            {permission}
                          </span>
                        ))}
                        {role.permissions.length > 3 && (
                          <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-gray-100 text-gray-800">
                            {t('roles.more', { count: role.permissions.length - 3 })}
                          </span>
                        )}
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <div className="text-center py-12">
                <Shield className="w-12 h-12 text-gray-400 mx-auto mb-4" />
                <h3 className="text-lg font-medium text-gray-900 mb-2">
                  {t('roles.noRolesFound')}
                </h3>
                <p className="text-gray-600">
                  {searchTerm ? t('roles.noRolesMatchSearch') : t('roles.noRolesCreatedYet')}
                </p>
              </div>
            )}
          </div>
        </div>

        {/* Available Permissions */}
        <div className="mt-8 bg-white rounded-lg shadow-sm border">
          <div className="px-6 py-4 border-b border-gray-200">
            <div className="flex items-center justify-between">
              <h3 className="text-lg font-medium text-gray-900">
                {t('roles.availablePermissions')}
              </h3>
              <select
                value={selectedCategory}
                onChange={(e) => setSelectedCategory(e.target.value)}
                className="px-3 py-2 border border-gray-300 rounded-md text-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
              >
                <option value="">{t('roles.allCategories')}</option>
                {categories.map((category) => (
                  <option key={category} value={category}>
                    {category}
                  </option>
                ))}
              </select>
            </div>
          </div>
          <div className="p-6">
            {isLoading ? (
              <div className="space-y-2">
                {Array.from({ length: 8 }).map((_, i) => (
                  <Skeleton key={i} className="h-8 w-full" />
                ))}
              </div>
            ) : (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {filteredPermissions.map((permission) => (
                  <div
                    key={permission.permission_id}
                    className="p-3 border border-gray-200 rounded-lg"
                  >
                    <div className="flex items-center mb-2">
                      <Lock className="w-4 h-4 text-gray-600 mr-2" />
                      <span className="font-medium text-gray-900">
                        {permission.action} - {permission.resource}
                      </span>
                    </div>
                    <div className="text-sm text-gray-600">
                      Category: <span className="font-medium">{permission.category}</span>
                    </div>
                    {permission.description && (
                      <div className="text-xs text-gray-500 mt-1">{permission.description}</div>
                    )}
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Modals */}
      <CreateRoleModal
        isOpen={isCreateModalOpen}
        onClose={() => setIsCreateModalOpen(false)}
        permissions={permissions}
        onSubmit={handleCreateRole}
        isLoading={false}
      />

      <AssignRoleModal
        isOpen={isAssignModalOpen}
        onClose={() => setIsAssignModalOpen(false)}
        roles={roles}
        onAssign={handleAssignRole}
      />
    </div>
  );
};

export default RoleManagementPage;
